import React from 'react';
import {useState} from 'react';
import quotes from "../quotes.json";
import "./body.css";

export default function WebpageBody() {

    const [search, setSearch] = useState("");


    const handleSearch = (event) => {
        const value = event.target.value;
        setSearch(value)
      }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log({ search });
    };

    const filterResults = (searchTerm, data) => {
        let filteredResults = []
        for (let i = 0; i < data.length; i++) {
            if (searchTerm in data[i].quote || searchTerm in data[i].author || searchTerm in data[i].birth_year || searchTerm in data[i].description) {
                filterResults.push(data[i])
            }

        } 
        return filteredResults
    }

    return (



        <>
        <section className="webpageBody"> 
            <p>Looking for something in particular? Search our treasure trove of quotes below:</p>
            <form onSubmit={handleSubmit}> 

            <label>
                <input 
                type="text" 
                name="search" 
                value={search} 
                onChange={(event) => handleSearch(event)}
                    />
            </label>
            <button type="submit">Search</button>
            </form>
        {<QuoteContainer/>}
        </section>
        </>
    )
}



