import React from 'react';
import {useState} from 'react';
import quotes from "../quotes.json";
import "./body.css";

const filterResults = (searchTerm) => {
    let filteredResults = []
    console.log("1")
    for (let i = 0; i < quotes.length; i++) {
        if (quotes[i].quote.includes(searchTerm) || quotes[i].author.includes(searchTerm)  || quotes[i].birth_year.includes(searchTerm)  || quotes[i].description.includes(searchTerm) ) {
            filterResults.push(quotes[i])
        }

    } 
    console.log("2")
    return filteredResults
}

export default function WebpageBody() {

    const [search, setSearch] = useState("");
    const [filteredQuotes, setFilteredQuotes] = useState(quotes);

    const handleSearch = (event) => {
        const value = event.target.value;
        setSearch(value)
      }

    const handleSubmit = (event) => {
        event.preventDefault();
        filterResults(search)
    };



    return (


        <>
        <section className="webpageBody"> 
            <p>Looking for something in particular? Search our treasure trove of quotes below:</p>
            <form onSubmit={handleSubmit}> 

            <label>
                <input 
                type="text" 
                name="search" 
                value={search} 
                onChange={(event) => handleSearch(event)}
                    />
            </label>
            <button type="submit">Search</button>
            </form>
        {<QuoteContainer/>}
        </section>
        </>
    )
}



export function QuoteContainer() {

    const filteredQuotes = quotes.filter(filterResults)
    return (
        <>
        <div>
        {filteredQuotes.map((quote) => (
            <div className="quoteContainer">
                <div className="quoteItem">
                    <p>{quote.quote}</p>
                    <p className="quoteAuthor"><strong>{quote.author} (b. {quote.birth_year})</strong>, {quote.description}</p>
                    </div>
                </div>

            ))}
            </div>
        </>

    )
}
